import axios from "axios";
const API_URL = "http://localhost:8080/api/cart";

class CartService {
  // 獲取購物車內容
  getCart() {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.get(API_URL, {
      headers: {
        Authorization: token,
      },
    });
  }

  // 添加商品到購物車
  addToCart(beanID) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    console.log("Sending request with token:", user.token);

    return axios.post(
      API_URL + "/add",
      { beanID },
      {
        headers: {
          Authorization: token,
        },
      }
    );
  }

  // 更新購物車商品數量
  updateQuantity(productId, quantity) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.put(
      API_URL + `/update/${productId}`,
      { quantity },
      {
        headers: {
          Authorization: token,
        },
      }
    );
  }

  // 從購物車移除商品
  removeFromCart(productId) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.delete(API_URL + `/remove/${productId}`, {
      headers: {
        Authorization: token,
      },
    });
  }

  // 清空購物車
  clearCart() {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.delete(API_URL + "/clear", {
      headers: {
        Authorization: token,
      },
    });
  }
}

export default new CartService();
