import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ArticleService from "../services/article-service";
import "../css/communicate.css";

const CommunicateComponent = ({ currentUser }) => {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [expandedArticles, setExpandedArticles] = useState({});
  const [showComments, setShowComments] = useState({});

  // 獲取所有文章
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await ArticleService.getAllArticles();
        setArticles(response.data);
        setLoading(false);
      } catch (error) {
        setMessage("獲取文章失敗");
        setLoading(false);
      }
    };
    fetchArticles();
  }, []);

  // 發表評論
  const handleCommentSubmit = async (articleId) => {
    try {
      const response = await ArticleService.postComment(articleId, {
        content: newComment,
      });
      const updatedArticles = articles.map((article) =>
        article._id === articleId
          ? { ...article, comments: [...article.comments, response.data] }
          : article
      );
      setArticles(updatedArticles);
      setNewComment("");
    } catch (error) {
      setMessage("評論發表失敗");
    }
  };

  const toggleContent = (articleId) => {
    setExpandedArticles((prev) => ({
      ...prev,
      [articleId]: !prev[articleId],
    }));
  };

  const toggleComments = (articleId) => {
    setShowComments((prev) => ({
      ...prev,
      [articleId]: !prev[articleId],
    }));
  };

  if (loading) return <div className="loading">載入中...</div>;

  return (
    <div className="communicate-container">
      <div className="communicate-header">
        <h1 className="page-title">咖啡交流區</h1>
        {currentUser && (
          <button
            className="post-article-btn"
            onClick={() => navigate("/post-article")}
          >
            <i className="fas fa-plus"></i>
            <span>發表文章</span>
          </button>
        )}
      </div>

      {/* 文章列表 */}
      <div className="articles-list">
        {articles.map((article) => (
          <div key={article._id} className="article-card">
            <div className="article-header">
              <h3>{article.title}</h3>
              <div className="article-meta">
                <span>{article.author.username}</span>
                <span>{new Date(article.createdAt).toLocaleDateString()}</span>
                <span
                  className="comments-count"
                  onClick={() => toggleComments(article._id)}
                >
                  {article.comments.length} 則留言
                </span>
              </div>
            </div>

            <div
              className={`article-content ${
                !expandedArticles[article._id] ? "collapsed" : ""
              }`}
            >
              {article.content}
            </div>
            {article.content.length > 100 && (
              <div className="more-btn-container">
                <button
                  className="more-btn"
                  onClick={() => toggleContent(article._id)}
                >
                  {expandedArticles[article._id] ? "收起" : "MORE"}
                </button>
              </div>
            )}

            {/* 留言區塊 */}
            {showComments[article._id] && (
              <div className="comments-section">
                <h4>留言</h4>
                {article.comments.length > 0 ? (
                  article.comments.map((comment, index) => (
                    <div key={index} className="comment">
                      <div className="comment-header">
                        <span className="comment-author">
                          {comment.author.username}
                        </span>
                        <span className="comment-date">
                          {new Date(comment.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <div className="comment-content">{comment.content}</div>
                    </div>
                  ))
                ) : (
                  <div className="no-comments">暫無留言</div>
                )}

                {/* 發表留言 */}
                {currentUser && (
                  <div className="post-comment">
                    <textarea
                      placeholder="發表留言..."
                      value={newComment}
                      onChange={(e) => setNewComment(e.target.value)}
                    />
                    <button
                      className="comment-btn"
                      onClick={() => handleCommentSubmit(article._id)}
                    >
                      發表留言
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* {message && <div className="message">{message}</div>} */}
    </div>
  );
};

export default CommunicateComponent;
