import React from "react";
import { useNavigate } from "react-router-dom";

const HomeComponent = () => {
  const navigate = useNavigate();
  const handlequestion = () => {
    const isConfirmed = window.confirm("您是否已經有帳號？");

    if (isConfirmed) {
      // 用戶選擇"是"，導向登入頁面
      navigate("/login");
    } else {
      // 用戶選擇"否"，導向註冊頁面
      navigate("/register");
    }
  };
  return (
    <main>
      <div className="container py-4">
        <div className="p-5 mb-4 bg-light rounded-3">
          <div className="container-fluid py-5">
            <h1 className="display-5 fw-bold">咖啡論壇＆交易平台</h1>
            <p className="col-md-8 fs-4">
              本平台使用 React.js 作為建構前端介面，Express.js作為web框架，
              Node.js、MongoDB作為資料庫。
            </p>
            <button className="btn btn-primary btn-lg" type="button">
              看看它怎麼運作。
            </button>
          </div>
        </div>

        <div className="row align-items-md-stretch">
          <div className="col-md-6">
            <div className="h-100 p-5 text-white bg-dark rounded-3">
              <h2>咖啡愛好者</h2>
              <p>
                您可以註冊一個帳號，並開始在論壇發表文章，與其他咖啡愛好者交流，以及購買來自各地的咖啡豆。
              </p>
              <button
                className="btn btn-outline-light"
                type="button"
                onClick={() => {
                  handlequestion();
                }}
              >
                登錄會員、或者註冊一個帳號
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div className="h-100 p-5 bg-light border rounded-3">
              <h2>咖啡商家</h2>
              <p>
                透過註冊成為咖啡商家，提供高品質的咖啡熟豆，也可以在論壇發表文章，分享咖啡知識。
              </p>
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => {
                  handlequestion();
                }}
              >
                註冊成為咖啡商家，讓大家認識你
              </button>
            </div>
          </div>
        </div>

        <footer className="pt-3 mt-4 text-muted border-top">
          &copy; 2023 Wilson Ren
        </footer>
      </div>
    </main>
  );
};

export default HomeComponent;
