import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import AuthService from "../services/auth-service";
import "../css/nav.css";
import { useCart } from "../context/CartContext";
import CartService from "../services/cart-service";

const NavComponent = ({ currentUser, setCurrentUser }) => {
  const location = useLocation();
  const { cartItemCount, updateCartItemCount } = useCart();

  const handleLogout = () => {
    AuthService.logout();
    window.alert("登出成功，返回首頁");
    setCurrentUser(null);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  useEffect(() => {
    const getCartCount = async () => {
      if (currentUser && currentUser.user.role === "customer") {
        try {
          const response = await CartService.getCart();
          const count = response.data.items.reduce((total, item) => {
            return total + item.quantity;
          }, 0);
          updateCartItemCount(count);
        } catch (error) {
          console.error("獲取購物車數量失敗:", error);
        }
      }
    };

    getCartCount();
  }, [currentUser, updateCartItemCount]);

  return (
    <nav className="nav">
      <div className="nav__container">
        <Link
          className={`nav__brand ${isActive("/") ? "nav__brand--active" : ""}`}
          to="/"
        >
          Brand
        </Link>

        <button
          className="nav__toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="nav__toggler-icon"></span>
        </button>

        <div className="nav__collapse" id="navbarNav">
          <ul className="nav__list">
            <li className="nav__item">
              <Link
                className={`nav__link ${
                  isActive("/products") ? "nav__link--active" : ""
                }`}
                to="/products"
              >
                所有商品
              </Link>
            </li>
            {currentUser && currentUser.user.role === "store" && (
              <li className="nav__item">
                <Link
                  className={`nav__link ${
                    isActive("/storeProducts") ? "nav__link--active" : ""
                  }`}
                  to="/storeProducts"
                >
                  商品管理
                </Link>
              </li>
            )}
            <li className="nav__item">
              <Link
                className={`nav__link ${
                  isActive("/knowledge") ? "nav__link--active" : ""
                }`}
                to="/knowledge"
              >
                咖啡知識
              </Link>
            </li>
            <li className="nav__item">
              <Link
                className={`nav__link ${
                  isActive("/articles") ? "nav__link--active" : ""
                }`}
                to="/articles"
              >
                咖啡交流
              </Link>
            </li>
          </ul>

          <ul className="nav__list nav__list--right">
            {currentUser && (
              <li className="nav__item">
                <Link
                  className={`nav__profile ${
                    isActive("/profile") ? "nav__profile--active" : ""
                  }`}
                  to="/profile"
                >
                  <i className="fas fa-user"></i>
                </Link>
              </li>
            )}
            {currentUser && currentUser.user.role === "customer" && (
              <li className="nav__item">
                <Link
                  className={`nav__cart ${
                    isActive("/cart") ? "nav__cart--active" : ""
                  }`}
                  to="/cart"
                >
                  <i className="fas fa-shopping-cart"></i>
                  {cartItemCount > 0 && (
                    <span className="nav__cart-badge">{cartItemCount}</span>
                  )}
                </Link>
              </li>
            )}
            {!currentUser && (
              <>
                <li className="nav__item">
                  <Link
                    className={`nav__member ${
                      isActive("/login") ? "nav__member--active" : ""
                    }`}
                    to="/login"
                  >
                    登入
                  </Link>
                </li>
                <li className="nav__item">
                  <Link
                    className={`nav__member ${
                      isActive("/register") ? "nav__member--active" : ""
                    }`}
                    to="/register"
                  >
                    註冊
                  </Link>
                </li>
              </>
            )}
            {currentUser && (
              <li className="nav__item">
                <Link onClick={handleLogout} className="nav__member" to="/">
                  登出
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default NavComponent;
