import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import BeansService from "../services/beans-service";
import "../css/product.css";

const ProductComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [beanData, setBeanData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let _id;
    if (currentUser) {
      _id = currentUser.user._id;
      if (currentUser.user.role === "store") {
        BeansService.getStoreBeans(_id)
          .then((data) => {
            setBeanData(data.data);
            setLoading(false);
          })
          .catch((error) => {
            console.log(error);
            setLoading(false);
          });
      } else if (currentUser.user.role === "customer") {
        BeansService.getPurchaseHistory(_id)
          .then((data) => {
            setBeanData(data.data);
            setLoading(false);
          })
          .catch((error) => {
            console.log(error);
            setLoading(false);
          });
      }
    }
  }, [currentUser]);

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  return (
    <div className="product-container">
      {currentUser && currentUser.user.role === "store" && (
        <div className="product-header">
          <h1 className="product-title">商品管理</h1>
          <button
            className="new-product-btn"
            onClick={() => navigate("/postproduct")}
          >
            <i className="fas fa-plus"></i>
            新增商品
          </button>
        </div>
      )}

      {currentUser && beanData && beanData.length > 0 ? (
        <div className="products-grid">
          {beanData.map((bean) => (
            <div className="product-card" key={bean._id}>
              <div className="product-card-body">
                <h5 className="product-card-title">{bean.title}</h5>
                <div className="price-tag">${bean.price}</div>

                <div className="product-info">
                  <i className="fas fa-weight"></i>
                  重量：{bean.weight}g
                </div>

                <div className="product-info">
                  <i className="fas fa-mortar-pestle"></i>
                  處理法：{bean.processing}
                </div>

                <div className="product-info">
                  <i className="fas fa-fire"></i>
                  烘焙程度：{bean.roast}
                </div>

                <div className="product-info">
                  <i className="fas fa-seedling"></i>
                  品種：{bean.cultivar}
                </div>

                <p className="product-description">{bean.description}</p>

                <div className="sales-info">
                  <i className="fas fa-chart-line"></i>
                  已售出：
                  <span
                    style={{ paddingTop: "0.8rem" }}
                    className="sales-count"
                  >
                    {bean.customers.length}
                  </span>
                </div>
              </div>

              <button
                className="edit-product-btn"
                onClick={() => navigate("/editproduct")}
              >
                編輯商品
              </button>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-products">目前沒有商品</div>
      )}
    </div>
  );
};

export default ProductComponent;
