import "../css/home.css";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";

const HomeComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const [showMore, setShowMore] = useState(false);

  const handlequestion = () => {
    setCurrentUser(AuthService.getCurrentUser());
    console.log(currentUser);
    if (currentUser) {
      window.alert("您已經登入，跳轉至個人頁面");
      navigate("/profile");
    } else {
      setShowQuestionModal(true);
    }
  };

  const QuestionModal = () => (
    <>
      <div
        className={`modal fade ${showQuestionModal ? "show" : ""}`}
        style={{ display: showQuestionModal ? "block" : "none" }}
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">會員登入</h5>
            </div>
            <div className="modal-body text-center">
              <p className="mb-4">您是否已經有帳號？</p>
              <div className="d-flex justify-content-center gap-3">
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => {
                    setShowQuestionModal(false);
                    navigate("/login");
                  }}
                >
                  是
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setShowQuestionModal(false);
                    navigate("/register");
                  }}
                >
                  否
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {showQuestionModal && <div className="modal-backdrop fade show"></div>}
    </>
  );

  return (
    <main>
      <div className="container py-4">
        <div className="p-5 mb-4 bg-light rounded-3">
          <div className="container-fluid py-5">
            <h1 className="display-5 fw-bold">咖啡論壇＆交易平台</h1>
            <p className="col-md-8 fs-4">
              隨著咖啡文化的普及，咖啡已經成為許多人生活中不可或缺的一部分，咖啡愛好者們也開始對咖啡豆品質有更高的要求
              近年來，越來越多自家烘焙的咖啡館如雨後春筍般出現，即便是同一隻咖啡豆，不同烘豆師展現出的特色也會有所不同，
              即便是自家烘焙的小店，也能透過本平台讓更多人認識，並提供各位咖啡愛好者有更多選擇。
            </p>
            <button
              className="btn btn-primary btn-lg"
              type="button"
              onClick={() => setShowMore(!showMore)}
            >
              {showMore ? "收起內容" : "ABOUT MORE"}
            </button>
            <div
              className={`collapse ${showMore ? "show" : ""}`}
              style={{
                transition: "all 0.3s ease-in-out",
                marginTop: "20px",
              }}
            >
              <div className="card card-body">
                <h3>平台特色</h3>
                <div></div>
                <div className="row mt-3">
                  <div className="col-md-4">
                    <h5>🛒 購物功能</h5>
                    <p>提供多樣化的咖啡豆選擇，支援購物車功能</p>
                  </div>
                  <div className="col-md-4">
                    <h5>💬 論壇交流</h5>
                    <p>咖啡愛好者的交流平台，分享心得與經驗</p>
                  </div>
                  <div className="col-md-4">
                    <h5>🏪 商家系統</h5>
                    <p>商家可以管理商品、查看訂單狀態</p>
                  </div>
                </div>
                <div className="row mt-3">
                  <div className="col-md-4">
                    <h5>👤 會員系統</h5>
                    <p>完整的會員註冊與管理功能</p>
                  </div>
                  <div className="col-md-4">
                    <h5>🔒 安全性</h5>
                    <p>使用 JWT 進行身份驗證與授權</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="row align-items-md-stretch">
          <div className="col-md-6">
            <div className="h-100 p-5 text-white bg-dark rounded-3">
              <h2>咖啡愛好者</h2>
              <p>
                您可以註冊一個帳號，並開始在論壇發表文章，與其他咖啡愛好者交流，以及購買來自各地的咖啡豆。
              </p>
              <button
                className="btn btn-outline-light"
                type="button"
                onClick={() => {
                  handlequestion();
                }}
              >
                登錄會員、或者註冊一個帳號
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div className="h-100 p-5 bg-light border rounded-3">
              <h2>咖啡商家</h2>
              <p>
                透過註冊成為咖啡商家，提供高品質的咖啡熟豆，也可以在論壇發表文章，分享咖啡知識。
              </p>
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => {
                  handlequestion();
