import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import CartService from "../services/cart-service";
import axios from "axios";
import "../css/products.css";

const PublicProfileComponent = (props) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [userProfile, setUserProfile] = useState(null);
  const [userProducts, setUserProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  let { currentUser, setCurrentUser } = props;

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await AuthService.getUserProfile(id);
        setUserProfile(response.data);

        // 如果是商家，獲取其商品
        if (response.data.role === "store") {
          const productsResponse = await axios.get(
            `http://localhost:8080/api/beans/public/store/${id}`
          );
          setUserProducts(productsResponse.data);
        }
        setLoading(false);
      } catch (error) {
        console.error("獲取用戶資料失敗:", error);
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [id]);

  if (loading) {
    return <div className="products">載入中...</div>;
  }

  return (
    <div className="products">
      <div className="products__header">
        <h1 className="products__title">{userProfile?.username} 的個人頁面</h1>
        <div className="products__user-info">
          <p>身份：{userProfile?.role === "store" ? "商家" : "一般會員"}</p>
          <p>
            加入時間：{new Date(userProfile?.createdAt).toLocaleDateString()}
          </p>
        </div>
      </div>

      {userProfile?.role === "store" && (
        <div className="products__grid">
          {userProducts.map((bean) => (
            <div className="products__card" key={bean._id}>
              <img
                src={`http://localhost:8080${bean.image}`}
                alt={bean.title}
                className="products__image"
              />
              <div className="products__card-content">
                <h5 className="products__card-title">{bean.title}</h5>
                <div className="products__info">
                  <span className="products__info-label">{bean.weight}g</span>
                  <span className="products__price">$ {bean.price}</span>
                </div>

                <div className="products__store">
                  <p className="products__sales">
                    已售出：{bean.customers.length} 件
                  </p>
                  <button
                    className="products__button products__button--detail"
                    onClick={() => handleDetail(bean._id)}
                  >
                    了解更多
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PublicProfileComponent;
