import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import "../css/register.css";

const RegisterComponent = () => {
  const navigate = useNavigate();
  let [username, setUsername] = useState("");
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");
  let [role, setRole] = useState("");
  let [message, setMessage] = useState("");

  const handleUsername = (e) => {
    setUsername(e.target.value);
  };
  const handleEmail = (e) => {
    setEmail(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };
  const handleRole = (e) => {
    setRole(e.target.value);
  };
  const handleRegister = () => {
    AuthService.register(username, email, password, role)
      .then((response) => {
        window.alert("Register success");
        console.log(response.data);
        navigate("/login");
      })
      .catch((e) => {
        setMessage(e.response.data);
      });
  };
  return (
    <div className="register-container">
      <h2 className="register-title">會員註冊</h2>
      {message && <div className="alert alert-danger">{message}</div>}

      <div className="form-group">
        <label htmlFor="username">用戶名稱 / 商家名稱</label>
        <input
          onChange={handleUsername}
          type="text"
          className="form-control"
          name="username"
          placeholder="用戶名稱 / 商家名稱"
        />
      </div>

      <div className="form-group">
        <label htmlFor="email">電子信箱</label>
        <input
          onChange={handleEmail}
          type="email"
          className="form-control"
          name="email"
          placeholder="Ex : coffee2025@gmail.com"
        />
      </div>

      <div className="form-group">
        <label htmlFor="password">密碼</label>
        <input
          onChange={handlePassword}
          type="password"
          className="form-control"
          name="password"
          placeholder="至少6個字元"
        />
      </div>

      <div className="form-group">
        <label>身份</label>
        <div className="role-selector">
          <div
            className={`role-option ${role === "customer" ? "selected" : ""}`}
            onClick={() => setRole("customer")}
          >
            一般會員
          </div>
          <div
            className={`role-option ${role === "store" ? "selected" : ""}`}
            onClick={() => setRole("store")}
          >
            商家會員
          </div>
        </div>
      </div>

      <button onClick={handleRegister} className="register-btn">
        註冊會員
      </button>
    </div>
  );
};

export default RegisterComponent;
