import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import CartService from "../services/cart-service";
import AuthService from "../services/auth-service";
import "../css/allproducts.css";
import { useCart } from "../context/CartContext";

const AllproductsComponent = (props) => {
  let { currentUser, setCurrentUser } = props;
  const navigate = useNavigate();
  const [beans, setBeans] = useState([]);
  const { updateCartItemCount } = useCart();

  const handletoaddTocart = async (beanID) => {
    try {
      if (!currentUser) {
        window.alert("請先登入");
        navigate("/login");
        return;
      }
      if (currentUser && currentUser.user.role === "store") {
        window.alert("商家無法使用此功能，請使用一般帳號登入");
        AuthService.logout();
        setCurrentUser(null);
        navigate("/login");
        return;
      }

      console.log("Adding bean to cart:", beanID);
      const response = await CartService.addToCart(beanID);
      console.log("response", response);

      if (response.data) {
        window.alert("成功加入購物車！");

        // 添加這段代碼 - 成功添加商品後更新購物車數量
        try {
          const cartResponse = await CartService.getCart();
          const total = cartResponse.data.items.reduce(
            (sum, item) => sum + item.quantity,
            0
          );
          updateCartItemCount(total);
        } catch (error) {
          console.error("更新購物車數量失敗:", error);
        }
      }
    } catch (e) {
      console.log(e);
      if (e.response?.status === 400) {
        window.alert(e.response.data.message);
      } else {
        window.alert("加入購物車失敗，請稍後再試");
      }
    }
  };
  const handleLogout = () => {
    AuthService.logout(); // remove user from local storage
    window.alert("請使用一般會員登入");
    setCurrentUser(null);
    navigate("/login");
  };

  const handleDetail = (beanId) => {
    navigate(`/detail/${beanId}`);
  };

  const getAllProducts = async () => {
    try {
      const response = await axios.get(
        "http://localhost:8080/api/beans/public"
      );
      console.log(response.data);
      setBeans(response.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getAllProducts();
  }, [currentUser]);

  return (
    <div className="products-container">
      <h1 className="products-title">All Products</h1>
      <div className="products-grid">
        {beans.map((bean) => (
          <div className="product-card" key={bean._id}>
            <div className="card-body">
              <h5 className="card-title">{bean.title}</h5>
              <p className="price">${bean.price}</p>

              <div className="product-info-container">
                <div className="product-info">
                  <span className="info-label">重量：</span>
                  <span>{bean.weight}g</span>
                </div>

                <div className="product-info">
                  <span className="info-label">處理法：</span>
                  <span>{bean.processing}</span>
                </div>

                <div className="product-info">
                  <span className="info-label">烘焙程度：</span>
                  <span>{bean.roast}</span>
                </div>

                <div className="product-info">
                  <span className="info-label">品種：</span>
                  <span>{bean.cultivar}</span>
                </div>
              </div>

              <div className="store-info">
                <p className="store-name">
                  店家：{bean.store?.username || "未知店家"}
                </p>
                <p className="sales-count">
                  已售出：{bean.customers.length} 件
                </p>
                <button
                  className="detail-btn"
                  onClick={() => handleDetail(bean._id)}
                >
                  了解更多
                </button>

                {currentUser && currentUser.user.role === "store" ? (
                  <button
                    className="change-to-customer-btn"
                    onClick={() => handleLogout()}
                  >
                    使用一般會員登入並購買
                  </button>
                ) : (
                  <button
                    className="add-to-cart-btn"
                    onClick={() => handletoaddTocart(bean._id)}
                  >
                    加入購物車
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllproductsComponent;
