import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import BeansService from "../services/beans-service";
import "../css/editProduct.css";

const EditProductComponent = ({ currentUser }) => {
  const navigate = useNavigate();
  const { beanId } = useParams();
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [beanData, setBeanData] = useState({
    title: "",
    weight: "",
    cultivar: "",
    processing: "",
    roast: "",
    description: "",
    price: "",
    image: null,
  });

  useEffect(() => {
    if (location.state) {
      setBeanData(location.state.beanData);
      if (location.state.beanData.image) {
        setImagePreview(
          `http://localhost:8080${location.state.beanData.image}`
        );
      }
      setLoading(false);
    } else {
      const loadBeanData = async () => {
        try {
          const response = await BeansService.getBeanById(beanId);
          setBeanData(response.data);
          if (response.data.image) {
            setImagePreview(`http://localhost:8080${response.data.image}`);
          }
          setLoading(false);
        } catch (error) {
          setMessage("載入商品資料失敗");
          setLoading(false);
        }
      };
      loadBeanData();
    }
  }, [beanId, location.state]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBeanData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setBeanData((prev) => ({
        ...prev,
        image: file,
      }));

      // 創建預覽圖
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();

      // 添加所有欄位到 FormData，並確保正確的數據類型
      formData.append("title", beanData.title);
      formData.append("weight", parseInt(beanData.weight));
      formData.append("price", parseInt(beanData.price));
      formData.append("cultivar", beanData.cultivar);
      formData.append("processing", beanData.processing);
      formData.append("roast", beanData.roast);
      formData.append("description", beanData.description);

      // 如果有新圖片，添加到 formData
      if (beanData.image instanceof File) {
        formData.append("image", beanData.image);
      }

      await BeansService.updateBean(beanData._id, formData);
      window.alert("商品更新成功");
      navigate("/product");
    } catch (error) {
      console.error("更新錯誤:", error.response?.data || error.message);
      setMessage(error.response?.data || "更新失敗");
    }
  };

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  return (
    <div className="edit-product-container">
      <div className="edit-product-card">
        <h2 className="edit-title">編輯商品</h2>

        {message && <div className="alert alert-danger">{message}</div>}

        <form onSubmit={handleSubmit} className="edit-form">
          <div className="form-group">
            <label className="form-label">商品圖片</label>
            <div className="image-upload-container">
              {imagePreview && (
                <div className="image-preview-container">
                  <img
                    src={imagePreview}
                    alt="商品預覽"
                    className="image-preview"
                  />
                </div>
              )}
              <input
                type="file"
                name="image"
                accept="image/*"
                onChange={handleImageChange}
                className="form-control"
              />
              <small className="form-text text-muted">
                支援的格式：JPG、PNG、GIF（最大 5MB）
              </small>
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">商品名稱</label>
            <input
              type="text"
              name="title"
              className="form-control"
              value={beanData.title}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label className="form-label">重量 (g)</label>
              <input
                type="number"
                name="weight"
                className="form-control"
                value={beanData.weight}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">價格</label>
              <input
                type="number"
                name="price"
                className="form-control"
                value={beanData.price}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label className="form-label">品種</label>
              <input
                type="text"
                name="cultivar"
                className="form-control"
                value={beanData.cultivar}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">處理法</label>
              <input
                type="text"
                name="processing"
                className="form-control"
                value={beanData.processing}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">烘焙程度</label>
            <input
              type="text"
              name="roast"
              className="form-control"
              value={beanData.roast}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">商品描述</label>
            <textarea
              name="description"
              className="form-control"
              value={beanData.description}
              onChange={handleInputChange}
              rows="4"
            />
          </div>

          <div className="button-group">
            <button type="submit" className="submit-btn">
              更新商品
            </button>
            <button
              type="button"
              className="cancel-btn"
              onClick={() => navigate("/product")}
            >
              取消
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProductComponent;
