import React, { useState } from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import AuthService from "./services/auth-service";
import { CartProvider } from "./context/CartContext";
import Layout from "./components/Layout";

// 公開頁面
import HomeComponent from "./components/home-component";
import LoginComponent from "./components/login-component";
import RegisterComponent from "./components/register-component";
import ForgotPasswordComponent from "./components/forgotPassword-component";
import ResetPasswordComponent from "./components/resetPassword-component";
import KnowledgeComponent from "./components/knowledge-component";

// 商品相關
import AllproductsComponent from "./components/allproducts-component";
import BeanDetailComponent from "./components/beanDetail-component";
import ProductComponent from "./components/product-component";
import PostProductComponent from "./components/postProduct-component";
import EditProductComponent from "./components/editProduct-component";

// 文章相關
import ArticleComponent from "./components/article-component";
import ArticleDetailComponent from "./components/articleDetail-component";
import PostArticleComponent from "./components/postArticle-component";

// 用戶相關
import ProfileComponent from "./components/profile-component";
import EditProfileComponent from "./components/editProfile-component";
import CartComponent from "./components/cart-component";

// 路由保護組件
const PrivateRoute = ({ children, currentUser }) => {
  return currentUser ? children : <Navigate to="/login" />;
};

function App() {
  let [currentUser, setCurrentUser] = useState(AuthService.getCurrentUser());

  return (
    <CartProvider>
      <BrowserRouter>
        <Routes>
          <Route
            path="/"
            element={
              <Layout
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          >
            {/* 公開路由 */}
            <Route index element={<HomeComponent />} />
            <Route path="knowledge" element={<KnowledgeComponent />} />

            {/* 認證相關路由 */}
            <Route path="auth">
              <Route
                path="login"
                element={
                  <LoginComponent
                    currentUser={currentUser}
                    setCurrentUser={setCurrentUser}
                  />
                }
              />
              <Route path="register" element={<RegisterComponent />} />
              <Route
                path="forgot-password"
                element={<ForgotPasswordComponent />}
              />
              <Route
                path="reset-password/:token"
                element={<ResetPasswordComponent />}
              />
            </Route>

            {/* 商品相關路由 */}
            <Route path="products">
              <Route
                index
                element={
                  <AllproductsComponent
                    currentUser={currentUser}
                    setCurrentUser={setCurrentUser}
                  />
                }
              />
              <Route
                path=":beanId"
                element={
                  <BeanDetailComponent
                    currentUser={currentUser}
                    setCurrentUser={setCurrentUser}
                  />
                }
              />
              <Route
                path="post"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <PostProductComponent
                      currentUser={currentUser}
                      setCurrentUser={setCurrentUser}
                    />
                  </PrivateRoute>
                }
              />
              <Route
                path="edit/:id"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <EditProductComponent currentUser={currentUser} />
                  </PrivateRoute>
                }
              />
            </Route>

            {/* 文章相關路由 */}
            <Route path="articles">
              <Route
                index
                element={<ArticleComponent currentUser={currentUser} />}
              />
              <Route
                path=":id"
                element={<ArticleDetailComponent currentUser={currentUser} />}
              />
              <Route
                path="post"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <PostArticleComponent currentUser={currentUser} />
                  </PrivateRoute>
                }
              />
            </Route>

            {/* 用戶相關路由 (需要登入) */}
            <Route path="user">
              <Route
                path="profile"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <ProfileComponent
                      currentUser={currentUser}
                      setCurrentUser={setCurrentUser}
                    />
                  </PrivateRoute>
                }
              />
              <Route
                path="profile/edit"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <EditProfileComponent
                      currentUser={currentUser}
                      setCurrentUser={setCurrentUser}
                    />
                  </PrivateRoute>
                }
              />
              <Route
                path="cart"
                element={
                  <PrivateRoute currentUser={currentUser}>
                    <CartComponent
                      currentUser={currentUser}
                      setCurrentUser={setCurrentUser}
                    />
                  </PrivateRoute>
                }
              />
            </Route>

            {/* 404 頁面 */}
            <Route path="*" element={<NotFound />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </CartProvider>
  );
}

export default App;
