import axios from "axios";
const API_URL = "http://localhost:8080/api/";

class CartService {
  // 獲取購物車內容
  getCart() {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.get(API_URL + "/cart", {
      headers: {
        Authorization: token,
      },
    });
  }

  // 添加商品到購物車
  addToCart(productId, quantity) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.post(
      API_URL + "/cart/add",
      { productId, quantity },
      {
        headers: {
          Authorization: token,
        },
      }
    );
  }

  // 更新購物車商品數量
  updateQuantity(productId, quantity) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.put(
      API_URL + `/cart/update/${productId}`,
      { quantity },
      {
        headers: {
          Authorization: token,
        },
      }
    );
  }

  // 從購物車移除商品
  removeFromCart(productId) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.delete(API_URL + `/cart/remove/${productId}`, {
      headers: {
        Authorization: token,
      },
    });
  }

  // 清空購物車
  clearCart() {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }

    return axios.delete(API_URL + "/cart/clear", {
      headers: {
        Authorization: token,
      },
    });
  }
}

export default new CartService();
