import React from "react";
import { Link } from "react-router-dom";
import AuthService from "../services/auth-service";

const NavComponent = ({ currentUser, setCurrentUser }) => {
  const handleLogout = () => {
    AuthService.logout();
    window.alert("Logout success , navigate to home page");
    setCurrentUser(null);
  };

  console.log("NavComponent rendered", currentUser);

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            {!currentUser && (
              <li className="nav-item">
                <Link className="nav-link" to="/register">
                  註冊會員
                </Link>
              </li>
            )}

            {!currentUser && (
              <li className="nav-item">
                <Link className="nav-link" to="/login">
                  會員登入
                </Link>
              </li>
            )}

            {currentUser && (
              <li className="nav-item">
                <Link className="nav-link" to="/profile">
                  個人頁面
                </Link>
              </li>
            )}

            {currentUser && currentUser.user.role === "store" && (
              <li className="nav-item">
                <Link className="nav-link" to="/product">
                  商品管理
                </Link>
              </li>
            )}

            <li className="nav-item">
              <Link className="nav-link" to="/allproducts">
                所有商品
              </Link>
            </li>
          </ul>
          <ul className="navbar-nav ms-auto">
            {currentUser && currentUser.user.role === "customer" && (
              <li className="nav-item cart-item">
                <Link className="nav-link cart-link" to="/cart">
                  <i className="fas fa-shopping-cart"></i>
                </Link>
              </li>
            )}
            {currentUser && (
              <li className="nav-item">
                <Link onClick={handleLogout} className="nav-link" to="/">
                  登出
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default NavComponent;
