import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

import CartService from "../services/cart-service";
import AuthService from "../services/auth-service";
import axios from "axios";
import "../css/detail.css";
import { useCart } from "../context/CartContext";
const DetailComponent = ({ currentUser, setCurrentUser }) => {
  const { beanId } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [beanData, setBeanData] = useState(null);
  const [message, setMessage] = useState("");
  const { updateCartItemCount } = useCart();

  useEffect(() => {
    const loadBeanData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/beans/public/${beanId}`
        );
        setBeanData(response.data);
        setLoading(false);
      } catch (error) {
        setMessage("載入商品資料失敗");
        setLoading(false);
      }
    };
    loadBeanData();
  }, [beanId]);

  const handleAddToCart = async () => {
    try {
      if (!currentUser) {
        window.alert("請先登入");
        navigate("/login");
        return;
      }
      if (currentUser.user.role === "store") {
        window.alert("商家無法使用此功能，請使用一般帳號登入");
        AuthService.logout();
        setCurrentUser(null);
        navigate("/login");
        return;
      }

      const response = await CartService.addToCart(beanId);
      if (response.data) {
        window.alert("成功加入購物車！");

        try {
          const cartResponse = await CartService.getCart();
          const total = cartResponse.data.items.reduce(
            (sum, item) => sum + item.quantity,
            0
          );
          updateCartItemCount(total);
        } catch (error) {
          console.error("更新購物車數量失敗:", error);
        }
      }
    } catch (error) {
      if (error.response?.status === 400) {
        window.alert(error.response.data.message);
      } else {
        window.alert("加入購物車失敗，請稍後再試");
      }
    }
  };

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  if (!beanData) {
    return <div className="error-message">{message}</div>;
  }

  return (
    <div className="bean-detail-container">
      <div className="bean-detail-card">
        <div className="bean-detail-image-section">
          <img
            src={`http://localhost:8080${beanData.image}`}
            alt={beanData.title}
            className="bean-detail-image"
          />
        </div>

        <div className="bean-detail-header">
          <div className="bean-detail-title-section">
            <h1 className="bean-detail-title">{beanData.title}</h1>
            <div className="bean-detail-price-section">
              <span className="bean-detail-price-value">${beanData.price}</span>
            </div>
          </div>

          <div className="bean-detail-store-info">
            <span className="bean-detail-store-name">
              店家：{beanData.store?.username || "未知店家"}
            </span>
            <span className="bean-detail-sales-count">
              已售出：{beanData.customers?.length || 0} 件
            </span>
          </div>
        </div>

        <div className="bean-detail-content">
          <div className="bean-detail-info-grid">
            <div className="bean-detail-info-item">
              <i className="fas fa-weight"></i>
              <span className="bean-detail-info-label">重量</span>
              <span className="bean-detail-info-value">{beanData.weight}g</span>
            </div>

            <div className="bean-detail-info-item">
              <i className="fas fa-seedling"></i>
              <span className="bean-detail-info-label">品種</span>
              <span className="bean-detail-info-value">
                {beanData.cultivar}
              </span>
            </div>

            <div className="bean-detail-info-item">
              <i className="fas fa-mortar-pestle"></i>
              <span className="bean-detail-info-label">處理法</span>
              <span className="bean-detail-info-value">
                {beanData.processing}
              </span>
            </div>

            <div className="bean-detail-info-item">
              <i className="fas fa-fire"></i>
              <span className="bean-detail-info-label">烘焙程度</span>
              <span className="bean-detail-info-value">{beanData.roast}</span>
            </div>
          </div>

          <div className="bean-detail-description-section">
            <h3>商品描述</h3>
            <p className="bean-detail-description-text">
              {beanData.description}
            </p>
          </div>

          <div className="bean-detail-action-buttons">
            {currentUser && currentUser.user.role === "store" ? (
              <button
                className="bean-detail-login-customer-btn"
                onClick={() => {
                  AuthService.logout();
                  setCurrentUser(null);
                  navigate("/login");
                }}
              >
                使用一般會員登入並購買
              </button>
            ) : (
              <button
                className="bean-detail-add-to-cart-btn"
                onClick={handleAddToCart}
              >
                加入購物車
              </button>
            )}
            <button
              className="bean-detail-back-btn"
              onClick={() => navigate("/allproducts")}
            >
              返回商品列表
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailComponent;
