import "../css/home.css";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";

const HomeComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const [showMore, setShowMore] = useState(false);

  const handlequestion = () => {
    setCurrentUser(AuthService.getCurrentUser());
    if (currentUser) {
      window.alert("您已經登入，跳轉至個人頁面");
      navigate("/profile");
    } else {
      setShowQuestionModal(true);
    }
  };

  const QuestionModal = () => (
    <>
      <div
        className={`modal fade ${showQuestionModal ? "show" : ""}`}
        style={{ display: showQuestionModal ? "block" : "none" }}
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="home__modal">
            <div className="modal-header">
              <h5 className="home__modal-title">會員登入</h5>
            </div>
            <div className="modal-body text-center">
              <p className="mb-4">您是否已經有帳號？</p>
              <div className="d-flex justify-content-center gap-3">
                <button
                  className="home__button"
                  onClick={() => {
                    setShowQuestionModal(false);
                    navigate("/login");
                  }}
                >
                  是
                </button>
                <button
                  className="home__button home__button--secondary"
                  onClick={() => {
                    setShowQuestionModal(false);
                    navigate("/register");
                  }}
                >
                  否
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {showQuestionModal && <div className="modal-backdrop fade show"></div>}
    </>
  );

  return (
    <main className="home">
      <div className="container py-4">
        <section className="home__hero">
          <div className="container-fluid">
            <h1 className="home__title">咖啡交流 ＆ 交易平台</h1>
            <p className="home__description">
              隨著咖啡文化的普及，咖啡已經成為許多人生活中不可或缺的一部分，咖啡愛好者們也開始對咖啡豆品質有更高的要求。
              <br />
              近年來，越來越多自家烘焙的咖啡館如雨後春筍般出現，即便是同一隻咖啡豆，不同烘豆師展現出的特色也會有所不同，
              即便是自家烘焙的小店，也能透過本平台讓更多人認識，並提供各位咖啡愛好者有更多選擇。
            </p>
            <button
              className="home__button"
              onClick={() => setShowMore(!showMore)}
            >
              {showMore ? "收起內容" : "ABOUT MORE"}
            </button>

            {showMore && (
              <div className="home__features">
                <h3 className="home__section-title">平台特色</h3>
                <div className="row mt-4">
                  {features.map((feature, index) => (
                    <div key={index} className="col-md-4 mb-4">
                      <div className="home__feature-card">
                        <div className="home__feature-icon">{feature.icon}</div>
                        <h5 className="home__feature-title">{feature.title}</h5>
                        <p>{feature.description}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </section>

        <div className="row">
          <div className="col-md-6">
            <div className="home__user-card home__user-card--coffee">
              <h2 className="home__user-card-title">咖啡愛好者</h2>
              <p className="home__user-card-text">
                您可以註冊一個帳號，並開始在論壇發表文章，與其他咖啡愛好者交流，以及購買來自各地的咖啡豆。
              </p>
              <button className="home__user-button" onClick={handlequestion}>
                登錄會員、或者註冊一個帳號
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div className="home__user-card home__user-card--store">
              <h2 className="home__user-card-title">咖啡商家</h2>
              <p className="home__user-card-text">
                透過註冊成為咖啡商家，提供高品質的咖啡熟豆，也可以在論壇發表文章，分享咖啡知識。
              </p>
              <button className="home__user-button" onClick={handlequestion}>
                註冊成為咖啡商家，讓大家認識你
              </button>
            </div>
          </div>
        </div>

        <footer className="home__footer">&copy; 2025 Hank Lin</footer>
      </div>
      <QuestionModal />
    </main>
  );
};

const features = [
  {
    icon: "🛒",
    title: "購物功能",
    description: "提供多樣化的咖啡豆選擇，購買各地的咖啡豆",
  },
  {
    icon: "💬",
    title: "論壇交流",
    description: "咖啡愛好者的交流平台，分享心得與經驗",
  },
  {
    icon: "🏪",
    title: "商家系統",
    description: "商家可以管理商品、查看訂單狀態",
  },
  {
    icon: "👤",
    title: "會員系統",
    description: "會員註冊與管理功能(用戶／商家)",
  },
  {
    icon: "How to build ?",
    title: "",
    description:
      "本系統使用 React.js作為前端框架，Express.js為web框架，Node.js、MongoDB作為後端服務器。",
  },
];

export default HomeComponent;
