import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import AuthService from "../services/auth-service";
import axios from "axios";
import "../css/publicProfile.css";

const PublicProfileComponent = () => {
  const { id } = useParams();
  const [userProfile, setUserProfile] = useState(null);
  const [userProducts, setUserProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await AuthService.getUserProfile(id);
        setUserProfile(response.data);

        // 如果是商家，獲取其商品
        if (response.data.role === "store") {
          const productsResponse = await axios.get(
            `http://localhost:8080/api/beans/public/store/${id}`
          );
          setUserProducts(productsResponse.data);
          console.log(productsResponse.data);
        }
        setLoading(false);
      } catch (error) {
        console.error("獲取用戶資料失敗:", error);
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [id]);

  if (loading) {
    return <div className="public-profile__loading">載入中...</div>;
  }

  return (
    <div className="public-profile">
      <div className="public-profile__header">
        <h1 className="public-profile__title">{userProfile?.username}</h1>
        <div className="public-profile__info">
          <p className="public-profile__role">
            身份：{userProfile?.role === "store" ? "商家" : "一般會員"}
          </p>
          <p className="public-profile__date">
            加入時間：{new Date(userProfile?.createdAt).toLocaleDateString()}
          </p>
        </div>
      </div>

      {userProfile?.role === "store" && (
        <div className="public-profile__products">
          <h2 className="public-profile__subtitle">商品列表</h2>
          <div className="public-profile__products-grid">
            {userProducts.map((product) => (
              <div key={product._id} className="public-profile__product-card">
                <img
                  src={`http://localhost:8080${product.image}`}
                  alt={product.title}
                  className="public-profile__product-image"
                />
                <div className="public-profile__product-content">
                  <h3 className="public-profile__product-title">
                    {product.title}
                  </h3>
                  <p className="public-profile__product-price">
                    ${product.price}
                  </p>
                  <p className="public-profile__product-sales">
                    已售出：{product.customers.length} 件
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PublicProfileComponent;
