import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CartService from "../services/cart-service";

const CartComponent = ({ currentUser, setCurrentUser }) => {
  const [cartItems, setCartItems] = useState({ items: [] });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
    if (currentUser.user.role !== "customer") {
      navigate("/");
    }

    loadCart();
  }, [currentUser, navigate]);

  const loadCart = async () => {
    try {
      const response = await CartService.getCart();
      setCartItems(response.data);
      setLoading(false);
    } catch (err) {
      setMessage("獲取購物車失敗: " + err.response?.data?.message);
      setLoading(false);
    }
  };

  const handletominus = async (beanID) => {
    try {
      // 找到當前商品
      const currentItem = cartItems.items.find(
        (item) => item.beanID._id === beanID._id
      );

      if (currentItem.quantity === 1) {
        const isConfirmed = window.confirm(
          `是否要將 ${currentItem.beanID.title} 從購物車中移除？`
        );
        if (isConfirmed) {
          await CartService.removeFromCart(beanID._id);
        } else {
          return;
        }
      } else {
        // 數量大於1，減少數量
        await CartService.updateQuantity(beanID._id, currentItem.quantity - 1);
      }

      await loadCart();
      setMessage("數量更新成功");
    } catch (err) {
      console.error("更新失敗:", err);
      setMessage("減少數量失敗: " + err.response?.data?.message);
    }
  };
  const handletoplus = async (beanID) => {
    try {
      // 找到當前商品
      const currentItem = cartItems.items.find(
        (item) => item.beanID._id === beanID._id
      );

      // 數量大於1，增加數量
      await CartService.updateQuantity(beanID._id, currentItem.quantity + 1);

      await loadCart();
      setMessage("數量更新成功");
    } catch (err) {
      console.error("更新失敗:", err);
      setMessage("增加數量失敗: " + err.response?.data?.message);
    }
  };

  // const handleUpdateQuantity = async (beanID, quantity) => {
  //   try {
  //     if (quantity < 1) {
  //       await CartService.removeFromCart(beanID);
  //     } else {
  //       await CartService.updateQuantity(beanID, quantity);
  //     }
  //     loadCart();
  //     setMessage("數量更新成功");
  //   } catch (err) {
  //     setMessage("更新數量失敗: " + err.response?.data?.message);
  //   }
  // };

  const handleRemoveItem = async (beanID) => {
    try {
      // 找到當前商品
      const currentItem = cartItems.items.find(
        (item) => item.beanID._id === beanID._id
      );

      // 詢問是否確定要移除商品
      const isConfirmed = window.confirm(
        `確定要將 ${currentItem.beanID.title} 從購物車中移除嗎？`
      );

      if (!isConfirmed) {
        return; // 如果用戶取消，則不進行任何操作
      }

      await CartService.removeFromCart(beanID._id);
      await loadCart();
      setMessage("商品已移除");
    } catch (err) {
      setMessage("移除商品失敗: " + err.response?.data?.message);
    }
  };

  const calculateTotal = () => {
    return cartItems.items.reduce((total, item) => {
      return total + item.beanID.price * item.quantity;
    }, 0);
  };

  if (loading) {
    return <div>載入中...</div>;
  }

  return (
    <div className="cart-container">
      <h2>購物車</h2>
      {cartItems.items.length === 0 ? (
        <p>購物車是空的</p>
      ) : (
        <>
          {cartItems.items.map((item) => (
            <div key={item.beanID} className="col-md-6 mb-3">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">
                    {item.beanID.title} {item.beanID.weight}g
                  </h5>
                  <span>{item.beanID.store.username}</span>
                  <p className="card-text" style={{ color: "red" }}>
                    $ {item.price}
                  </p>
                </div>

                <div className="d-flex align-items-center mb-3">
                  <button
                    className="btn btn-sm btn-outline-secondary"
                    onClick={() => handletominus(item.beanID)}
                    disabled={loading}
                  >
                    -
                  </button>
                  <span className="mx-2">{item.quantity}</span>
                  <button
                    className="btn btn-sm btn-outline-secondary"
                    onClick={() => handletoplus(item.beanID)}
                    disabled={loading}
                  >
                    +
                  </button>
                </div>
                <button
                  className="btn btn-sm btn-outline-secondary"
                  onClick={() => handleRemoveItem(item.beanID)}
                >
                  移除
                </button>
              </div>
            </div>
          ))}
          <div className="cart-summary">
            <h3>總計: ${calculateTotal()}</h3>
            <button className="checkout-btn">結帳</button>
          </div>
        </>
      )}
    </div>
  );
};

export default CartComponent;
