import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../css/article.css";
import axios from "axios";
import ArticleService from "../services/article-service";

const ArticleComponent = ({ currentUser }) => {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const [expandedArticles, setExpandedArticles] = useState({});

  // 獲取所有文章
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/api/articles/public"
        );
        setArticles(response.data);
        setLoading(false);
      } catch (error) {
        setMessage("獲取文章失敗");
        setLoading(false);
      }
    };
    fetchArticles();
  }, []);

  const toggleContent = (articleId) => {
    setExpandedArticles((prev) => ({
      ...prev,
      [articleId]: !prev[articleId],
    }));
  };

  if (loading) return <div className="loading">載入中...</div>;

  return (
    <div className="article-container">
      <div className="article-big-header">
        <h1 className="article-title">咖啡交流區</h1>
        {currentUser && (
          <button
            className="post-article-btn"
            onClick={() => navigate("/post-article")}
          >
            <i className="fas fa-plus"></i>
            <span>發表文章</span>
          </button>
        )}
      </div>

      {/* 文章列表 */}
      <div className="articles-list">
        {articles.map((article) => (
          <div
            key={article._id}
            className="article-card"
            onClick={() => toggleContent(article._id)}
          >
            <div className="article-header">
              <h3
                onClick={(e) => {
                  e.stopPropagation();
                  navigate(`/article/${article._id}`);
                }}
                style={{ cursor: "pointer" }}
              >
                {article.title}
              </h3>
              <div className="article-meta">
                <span>{article.author.username}</span>
                <span>{new Date(article.createdAt).toLocaleDateString()}</span>
                <span className="comments-count">
                  {article.comments.length} 則留言
                </span>
              </div>
            </div>

            <div
              className={`article-content ${
                !expandedArticles[article._id] ? "collapsed" : ""
              }`}
            >
              {article.content}
              {expandedArticles[article._id] && (
                <div className="latest-comment">
                  <h4>最新留言</h4>
                  {article.comments.length > 0 ? (
                    <div className="comment">
                      <div className="comment-meta">
                        <span className="comment-author">
                          {
                            article.comments[article.comments.length - 1].author
                              .username
                          }
                          {
                            article.comments[article.comments.length - 1]
                              .content
                          }
                        </span>

                        <span className="comment-date">
                          {new Date(
                            article.comments[
                              article.comments.length - 1
                            ].createdAt
                          ).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ) : (
                    <div className="no-comments">暫無留言</div>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default ArticleComponent;
