import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../css/articles.css";
import axios from "axios";

const ArticleComponent = ({ currentUser }) => {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const [expandedArticles, setExpandedArticles] = useState({});

  // 獲取所有文章
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/api/articles/public"
        );
        setArticles(response.data);
        setLoading(false);
      } catch (error) {
        setMessage("獲取文章失敗");
        setLoading(false);
      }
    };
    fetchArticles();
  }, []);

  const toggleContent = (articleId) => {
    setExpandedArticles((prev) => ({
      ...prev,
      [articleId]: !prev[articleId],
    }));
  };

  if (loading) return <div className="loading">載入中...</div>;

  return (
    <div className="article-container">
      <div className="article-list">
        <div className="article-list__container">
          <div className="article-list__header">
            <h1 className="article-list__title">文章列表</h1>
            {currentUser && (
              <button
                className="article-list__post-btn"
                onClick={() => navigate("/articles/post")}
              >
                <i className="article-list__post-btn-icon fas fa-plus"></i>
                發布文章
              </button>
            )}
          </div>

          {articles.map((article) => (
            <div className="article-card" key={article._id}>
              <div className="article-card__header">
                <h3
                  className="article-card__title"
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/articles/${article._id}`);
                  }}
                  style={{ cursor: "pointer" }}
                >
                  {article.title}
                </h3>
                <div className="article-card__meta">
                  <span className="article-card__meta-item">
                    <i className="fas fa-user"></i>
                    {article.author.username}
                  </span>
                  <span className="article-card__meta-item">
                    <i className="fas fa-clock"></i>
                    {new Date(article.createdAt).toLocaleDateString()}
                  </span>
                  <span className="comments-count">
                    {article.comments.length} 則留言
                  </span>
                </div>
              </div>
              <div
                className={`article-card__content article-card__content--collapsed ${
                  !expandedArticles[article._id] ? "" : "expanded"
                }`}
              >
                {article.content}
              </div>

              <div className="comment-section">
                <h4 className="comment-section__title">最新留言</h4>
                {article.comments.length > 0 ? (
                  <div className="comment-section__item">
                    <div className="comment-section__meta">
                      <span className="comment-section__author">
                        {article.comments[0].author.username}
                      </span>
                      {new Date(
                        article.comments[0].createdAt
                      ).toLocaleDateString()}
                    </div>
                    {article.comments[0].content}
                  </div>
                ) : (
                  <div className="comment-section--empty">暫無留言</div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default ArticleComponent;
