import React, { useState, useEffect } from "react";
import CartService from "../services/cart-service";

const CartPage = () => {
  const [cart, setCart] = useState({ items: [] });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const response = await CartService.getCart();
      setCart(response.data);
      setLoading(false);
    } catch (err) {
      setMessage("獲取購物車失敗: " + err.response?.data?.message);
      setLoading(false);
    }
  };

  const updateQuantity = async (productId, quantity) => {
    try {
      await CartService.updateQuantity(productId, quantity);
      fetchCart();
    } catch (err) {
      setMessage("更新數量失敗: " + err.response?.data?.message);
    }
  };

  const removeItem = async (productId) => {
    try {
      await CartService.removeFromCart(productId);
      fetchCart();
    } catch (err) {
      setMessage("移除商品失敗: " + err.response?.data?.message);
    }
  };

  const calculateTotal = () => {
    return cart.items.reduce((total, item) => {
      return total + item.beanID.price * item.quantity;
    }, 0);
  };

  if (loading) {
    return <div>載入中...</div>;
  }

  return (
    <div className="cart-container">
      <h2>購物車</h2>
      {cart.items.length === 0 ? (
        <p>購物車是空的</p>
      ) : (
        <>
          {cart.items.map((item) => (
            <div key={item.beanID} className="cart-item">
              <img src={item.beanID.image} alt={item.beanID.name} />
              <div className="item-details">
                <h3>{item.beanID.name}</h3>
                <p>價格: ${item.beanID.price}</p>
                <div className="quantity-controls">
                  <button
                    onClick={() =>
                      updateQuantity(item.beanID, item.quantity - 1)
                    }
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() =>
                      updateQuantity(item.beanID, item.quantity + 1)
                    }
                  >
                    +
                  </button>
                </div>
                <button
                  onClick={() => removeItem(item.beanID)}
                  className="remove-btn"
                >
                  移除
                </button>
              </div>
            </div>
          ))}
          <div className="cart-summary">
            <h3>總計: ${calculateTotal()}</h3>
            <button className="checkout-btn">結帳</button>
          </div>
        </>
      )}
    </div>
  );
};

export default CartPage;
