import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import "../css/editProfile.css";

const EditProfileComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  useEffect(() => {
    // 確保用戶已登入
    if (!currentUser) {
      navigate("/login");
      return;
    }

    // 載入用戶資料
    setUserData({
      ...userData,
      username: currentUser.user.username,
      email: currentUser.user.email,
    });
    setLoading(false);
  }, [currentUser, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 儲存當前滾動位置
    const scrollPosition = window.scrollY;

    setMessage("");

    // 驗證表單
    if (userData.newPassword !== userData.confirmPassword) {
      setMessage("新密碼與確認密碼不匹配");
      // 恢復滾動位置
      window.scrollTo(0, scrollPosition);
      return;
    }

    try {
      setLoading(true);
      const updateData = {};

      if (userData.username !== currentUser.user.username) {
        updateData.username = userData.username;
      }

      if (userData.newPassword) {
        updateData.currentPassword = userData.currentPassword;
        updateData.newPassword = userData.newPassword;
      }

      // 如果沒有要更新的資料
      if (Object.keys(updateData).length === 0) {
        setMessage("沒有要更新的資料");
        setLoading(false);
        // 恢復滾動位置
        window.scrollTo(0, scrollPosition);
        return;
      }

      // 調用更新 API
      const response = await AuthService.updateProfile(updateData);

      // 更新本地存儲的用戶資料和 token
      const updatedUser = {
        token: response.data.token,
        user: response.data.user,
      };

      localStorage.setItem("user", JSON.stringify(updatedUser));
      setCurrentUser(updatedUser);

      // 清空密碼相關欄位
      setUserData((prev) => ({
        ...prev,
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      }));

      // 設置成功訊息
      setMessage("個人資料更新成功！");
    } catch (error) {
      let errorMessage = "更新失敗，請稍後再試";

      if (error.response) {
        errorMessage = error.response.data || errorMessage;
      }

      if (error.response?.status === 401) {
        errorMessage = "當前密碼不正確";
      }

      setMessage(errorMessage);
    } finally {
      setLoading(false);
      // 恢復滾動位置
      window.scrollTo(0, scrollPosition);
    }
  };

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-card">
        <h2 className="edit-profile-title">編輯個人資料</h2>

        <form onSubmit={handleSubmit} className="edit-profile-form">
          <div className="form-group">
            <label>用戶名稱</label>
            <input
              type="text"
              name="username"
              value={userData.username}
              onChange={handleInputChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group">
            <label>電子郵件</label>
            <input
              type="email"
              name="email"
              value={userData.email}
              className="form-control"
              disabled
              title="電子郵件不可修改"
            />
            <small className="form-text text-muted">電子郵件不可修改</small>
          </div>

          <div className="password-section">
            <h3>修改密碼</h3>
            <p className="text-muted">如不修改密碼，請留空</p>

            <div className="form-group">
              <label>當前密碼</label>
              <input
                type="password"
                name="currentPassword"
                value={userData.currentPassword}
                onChange={handleInputChange}
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label>新密碼</label>
              <input
                type="password"
                name="newPassword"
                value={userData.newPassword}
                onChange={handleInputChange}
                className="form-control"
                minLength="6"
              />
              <small className="form-text text-muted">
                密碼長度至少為 6 個字符
              </small>
            </div>

            <div className="form-group">
              <label>確認新密碼</label>
              <input
                type="password"
                name="confirmPassword"
                value={userData.confirmPassword}
                onChange={handleInputChange}
                className="form-control"
              />
            </div>
            {message && (
              <div
                className={`alert ${
                  message.includes("成功") ? "alert-success" : "alert-danger"
                }`}
              >
                {message}
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-save" disabled={loading}>
              {loading ? "儲存中..." : "儲存變更"}
            </button>

            <button
              type="button"
              className="btn-cancel"
              onClick={() => navigate("/profile")}
              disabled={loading}
            >
              取消
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProfileComponent;
