import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import AuthService from "../services/auth-service";
import "../css/nav.css";
import { useCart } from "../context/CartContext";
import CartService from "../services/cart-service";

const NavComponent = ({ currentUser, setCurrentUser }) => {
  const location = useLocation();
  const { cartItemCount, updateCartItemCount } = useCart();

  const handleLogout = () => {
    AuthService.logout();
    window.alert("登出成功，返回首頁");
    setCurrentUser(null);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  useEffect(() => {
    const getCartCount = async () => {
      if (currentUser && currentUser.user.role === "customer") {
        try {
          const response = await CartService.getCart();
          const count = response.data.items.reduce((total, item) => {
            return total + item.quantity;
          }, 0);
          updateCartItemCount(count);
        } catch (error) {
          console.error("獲取購物車數量失敗:", error);
        }
      }
    };

    getCartCount();
  }, [currentUser, updateCartItemCount]);

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light">
        <div className="container-fluid">
          <Link
            className={`navbar-brand ${isActive("/") ? "active" : ""}`}
            to="/"
          >
            Brand
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              {!currentUser && (
                <li className="nav-item">
                  <Link
                    className={`nav-link ${
                      isActive("/register") ? "active" : ""
                    }`}
                    to="/register"
                  >
                    註冊會員
                  </Link>
                </li>
              )}

              {!currentUser && (
                <li className="nav-item">
                  <Link
                    className={`nav-link ${isActive("/login") ? "active" : ""}`}
                    to="/login"
                  >
                    會員登入
                  </Link>
                </li>
              )}

              {currentUser && (
                <li className="nav-item">
                  <Link
                    className={`nav-link ${
                      isActive("/profile") ? "active" : ""
                    }`}
                    to="/profile"
                  >
                    個人頁面
                  </Link>
                </li>
              )}

              {currentUser && currentUser.user.role === "store" && (
                <li className="nav-item">
                  <Link
                    className={`nav-link ${
                      isActive("/product") ? "active" : ""
                    }`}
                    to="/product"
                  >
                    商品管理
                  </Link>
                </li>
              )}

              <li className="nav-item">
                <Link
                  className={`nav-link ${
                    isActive("/allproducts") ? "active" : ""
                  }`}
                  to="/allproducts"
                >
                  所有商品
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${
                    isActive("/knowledge") ? "active" : ""
                  }`}
                  to="/knowledge"
                >
                  咖啡知識
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${
                    isActive("/communicate") ? "active" : ""
                  }`}
                  to="/communicate"
                >
                  咖啡交流
                </Link>
              </li>
            </ul>
            <ul className="navbar-nav ms-auto">
              {currentUser && currentUser.user.role === "customer" && (
                <li className="nav-item cart-item">
                  <Link
                    className={`nav-link cart-link ${
                      isActive("/cart") ? "active" : ""
                    }`}
                    to="/cart"
                  >
                    <i className="fas fa-shopping-cart"></i>
                    {cartItemCount > 0 && (
                      <span className="cart-badge">{cartItemCount}</span>
                    )}
                  </Link>
                </li>
              )}
              {currentUser && (
                <li className="nav-item">
                  <Link onClick={handleLogout} className="nav-link" to="/">
                    登出
                  </Link>
                </li>
              )}
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavComponent;
