import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/articleDetail.css";

const ArticleDetailComponent = ({ currentUser }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showReplyModal, setShowReplyModal] = useState(false);
  const [replyContent, setReplyContent] = useState("");

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/articles/public/${id}`
        );
        setArticle(response.data);
        setLoading(false);
      } catch (error) {
        setError("文章載入失敗");
        setLoading(false);
      }
    };

    fetchArticle();
  }, [id]);

  const handleReplySubmit = async () => {
    try {
      const response = await axios.post(
        `http://localhost:8080/api/articles/public/${id}/comments`,
        { content: replyContent },
        {
          headers: {
            Authorization: localStorage.getItem("token"),
          },
        }
      );

      // 更新文章評論
      setArticle({
        ...article,
        comments: [...article.comments, response.data],
      });

      // 清空並關閉彈窗
      setReplyContent("");
      setShowReplyModal(false);
    } catch (error) {
      console.error("回覆發送失敗", error);
    }
  };

  if (loading) return <div className="loading">載入中...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!article) return <div className="error">找不到文章</div>;

  return (
    <div className="article-detail-container">
      <div className="article-detail-header">
        <button className="back-btn" onClick={() => navigate("/article")}>
          <i className="fas fa-arrow-left"></i> 返回
        </button>
        <h1>{article.title}</h1>
      </div>

      <div className="article-detail-meta">
        <span className="author">作者：{article.author.username}</span>
        <span className="date">
          發表於：{new Date(article.createdAt).toLocaleDateString()}
        </span>
      </div>

      <div className="article-detail-content">{article.content}</div>

      <div className="comments-section">
        <div className="comments-header">
          <h3>留言區 ({article.comments.length})</h3>
          <button className="reply-btn" onClick={() => setShowReplyModal(true)}>
            <i className="fas fa-reply"></i> 回覆
          </button>
        </div>

        {article.comments.map((comment, index) => (
          <div key={index} className="comment">
            <div className="comment-header">
              <span className="comment-author">{comment.author.username}</span>
              <span className="comment-date">
                {new Date(comment.createdAt).toLocaleDateString()}
              </span>
            </div>
            <div className="comment-content">{comment.content}</div>
          </div>
        ))}
      </div>

      {/* 回覆彈出框 */}
      {showReplyModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>發表回覆</h3>
            <textarea
              value={replyContent}
              onChange={(e) => setReplyContent(e.target.value)}
              placeholder="請輸入您的回覆..."
            />
            <div className="modal-buttons">
              <button
                className="article-detail-cancel-btn"
                onClick={() => {
                  setShowReplyModal(false);
                  setReplyContent("");
                }}
              >
                取消
              </button>
              <button
                className="article-detail-submit-btn"
                onClick={handleReplySubmit}
                disabled={!replyContent.trim()}
              >
                發送
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ArticleDetailComponent;
