import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import CartService from "../services/cart-service";
import AuthService from "../services/auth-service";
// import beansService from "../services/beans-service";

const AllproductsComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [beans, setBeans] = useState([]);

  const handletoaddcart = async (productId, quantity, price) => {
    try {
      let currentUser = AuthService.getCurrentUser();
      console.log(currentUser);

      if (!currentUser) {
        window.alert("請先登入");
        navigate("/login");
        return;
      }
      if (currentUser && currentUser.user.role === "store") {
        window.alert("商家無法使用此功能，請使用一般帳號登入");
        AuthService.logout();
        setCurrentUser(null);
        navigate("/login");
        return;
      }

      const response = await CartService.addToCart(productId, 1, price);
      console.log(response);
      if (response.data) {
        window.alert("成功加入購物車！");
      }
    } catch (e) {
      console.log(e);
      if (e.response?.status === 400) {
        window.alert(e.response.data.message);
      } else {
        window.alert("加入購物車失敗，請稍後再試");
      }
    }
  };

  const getAllProducts = async () => {
    try {
      const response = await axios.get(
        "http://localhost:8080/api/beans/public"
      );
      console.log(response.data);
      setBeans(response.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getAllProducts();
  }, []);

  return (
    <div style={{ padding: "3rem" }}>
      <h1>All Products</h1>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {beans.map((bean) => (
          <div
            className="card"
            style={{ width: "15rem", margin: "1rem" }}
            key={bean._id}
          >
            <div className="card-body">
              <h5 className="card-title">{bean.title}</h5>
              <p className="card-text">${bean.price}</p>
              <p className="card-text">重量(g)：{bean.weight}</p>
              <p className="card-text">處理法：{bean.processing}</p>
              <p className="card-text">烘焙程度：{bean.roast}</p>
              <p className="card-text">品種：{bean.cultivar}</p>
              <p className="card-text">{bean.description}</p>
              <p className="card-text">
                店家：{bean.store?.username || "未知店家"}
              </p>
              <p className="card-text">已售出：{bean.customers.length}</p>
              <button
                className="btn btn-primary"
                onClick={() => handletoaddcart(bean._id)}
              >
                加入購物車
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllproductsComponent;
