import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import BeansService from "../services/beans-service";

const ProductComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const handletopostproduct = () => {
    navigate("/postproduct");
  };
  const handleEditProduct = () => {
    navigate("/editproduct");
  };
  const [beanData, setBeanData] = useState(null);
  useEffect(() => {
    let _id;
    if (currentUser) {
      _id = currentUser.user._id;
      if (currentUser.user.role == "store") {
        BeansService.getStoreBeans(_id)
          .then((data) => {
            console.log(data);
            setBeanData(data.data);
          })
          .catch((error) => {
            console.log(error);
          });
      } else if (currentUser.user.role == "customer") {
        BeansService.getPurchaseHistory(_id)
          .then((data) => {
            console.log(data);
            setBeanData(data.data);
          })
          .catch((error) => {
            console.log(error);
          });
      }
    }
  }, []); // empty array means run when component is rendered
  return (
    <div style={{ padding: "3rem" }}>
      {currentUser && currentUser.user.role == "store" && (
        <div>
          <h1>Your Products</h1>
          <button className="btn btn-primary" onClick={handletopostproduct}>
            New Product
          </button>
        </div>
      )}
      {currentUser && beanData && beanData.length != 0 && (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {beanData.map((bean) => {
            return (
              <div
                className="card"
                style={{ width: "15rem", margin: "1rem" }}
                key={bean._id}
              >
                <div className="card-body">
                  <h5 className="card-title">{bean.title}</h5>
                  <p className="card-text">${bean.price}</p>
                  <p className="card-text">重量(g)：{bean.weight}</p>
                  <p className="card-text">處理法：{bean.processing}</p>
                  <p className="card-text">烘焙程度：{bean.roast}</p>
                  <p className="card-text">品種：{bean.cultivar}</p>
                  <p className="card-text">{bean.description}</p>
                  <p className="card-text">已售出：{bean.customers.length}</p>
                  <button
                    className="btn btn-primary"
                    onClick={handleEditProduct}
                  >
                    EditProduct
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ProductComponent;
