import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import CartService from "../services/cart-service";
import AuthService from "../services/auth-service";
import "../css/allproducts.css";
//import beansService from "../services/beans-service";

const AllproductsComponent = (props) => {
  let { currentUser, setCurrentUser } = props;
  const navigate = useNavigate();
  const [beans, setBeans] = useState([]);

  const handletoaddTocart = async (beanID) => {
    console.log(currentUser);
    try {
      if (!currentUser) {
        window.alert("請先登入");
        navigate("/login");
        return;
      }
      if (currentUser && currentUser.user.role === "store") {
        window.alert("商家無法使用此功能，請使用一般帳號登入");
        AuthService.logout();
        setCurrentUser(null);
        navigate("/login");
        return;
      }

      console.log("Adding bean to cart:", beanID);
      const response = await CartService.addToCart(beanID);
      console.log("response", response);

      if (response.data) {
        window.alert("成功加入購物車！");
      }
    } catch (e) {
      console.log(e);
      if (e.response?.status === 400) {
        window.alert(e.response.data.message);
      } else {
        window.alert("加入購物車失敗，請稍後再試");
      }
    }
  };

  const getAllProducts = async () => {
    try {
      const response = await axios.get(
        "http://localhost:8080/api/beans/public"
      );
      console.log(response.data);
      setBeans(response.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getAllProducts();
  }, []);

  return (
    <div className="products-container">
      <h1 className="products-title">All Products</h1>
      <div className="products-grid">
        {beans.map((bean) => (
          <div className="product-card" key={bean._id}>
            <div className="card-body">
              <h5 className="card-title">{bean.title}</h5>
              <p className="price">${bean.price}</p>

              <div className="product-info">
                <span className="info-label">重量：</span>
                {bean.weight}g
              </div>

              <div className="product-info">
                <span className="info-label">處理法：</span>
                {bean.processing}
              </div>

              <div className="product-info">
                <span className="info-label">烘焙程度：</span>
                {bean.roast}
              </div>

              <div className="product-info">
                <span className="info-label">品種：</span>
                {bean.cultivar}
              </div>

              <p className="product-info">{bean.description}</p>

              <p className="store-name">
                店家：{bean.store?.username || "未知店家"}
              </p>

              <p className="sales-count">已售出：{bean.customers.length} 件</p>

              <button
                className="add-to-cart-btn"
                onClick={() => handletoaddTocart(bean._id)}
              >
                加入購物車
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllproductsComponent;
